package elpoeta.bitfeather.modelo;

import elpoeta.bitfeather.util.Validar;
import java.util.List;

/**
 *
 * @author elpoeta
 */
public class Autor {
    	private Integer id;
	private String nombre;
        private String email;
        private String password;
        private String confirmPassword;
        private boolean is_activo;
        private String avatar;
	private List<Post> posts;
	
	public Autor() {
            this.avatar = "data:image/png;base64,";
	}

    public Integer getId() {
        return id;
    }

    public void setId(Integer id) {
        this.id = id;
    }

    public String getNombre() {
        return nombre;
    }

    public String getAvatar() {
        return avatar;
    }

    public void setAvatar(String avatar) {
        this.avatar = avatar;
    }

    public void setNombre(String nombre) {
         if(Validar.isNullOEspaciosEnBlanco(nombre)){
            throw new IllegalArgumentException("Verificar Nombre"); 
        }
        this.nombre = nombre;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        if(!Validar.isValidEmail(email)){
            throw new IllegalArgumentException("Verificar email");  
        }

        this.email = email;
    }

    public String getPassword() {
        return password;
    }

    public void setPassword(String password) {
        if(Validar.isNullOEspaciosEnBlanco(password)){
            throw new IllegalArgumentException("Verificar Password"); 
        }
        this.password = password;
    }

    public boolean isIs_activo() {
        return is_activo;
    }

    public void setIs_activo(boolean is_activo) {
        this.is_activo = is_activo;
    }

    public List<Post> getPosts() {
        return posts;
    }

    public void setPosts(List<Post> posts) {
        this.posts = posts;
    }

    public String getConfirmPassword() {
        return confirmPassword;
    }

    public void setConfirmPassword(String confirmPassword) {
        if(Validar.isNullOEspaciosEnBlanco(confirmPassword)){
            throw new IllegalArgumentException("Verificar Password"); 
        }
        this.confirmPassword = confirmPassword;
    }

    @Override
    public String toString() {
        return "Autor{" + "id=" + id + ", nombre=" + nombre + ", email=" + email + ", password=" + password + ", confirmPassword=" + confirmPassword + ", is_activo=" + is_activo + ", avatar=" + avatar + ", posts=" + posts + '}';
    }

  
}
